import React, { useState, useEffect } from 'react';

function ResultTable({ keyword, user, onAdded }) {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [editing, setEditing] = useState(null);

    // Tải dữ liệu 1 lần khi component mount
    useEffect(() => {
        fetch("https://jsonplaceholder.typicode.com/users")
            .then(res => res.json())
            .then(data => {
                setUsers(data);
                setLoading(false);
            });
    }, []);

    // Thêm người dùng mới
    useEffect(() => {
        if (user) {
            setUsers((prev) => [...prev, { ...user, id: prev.length + 1 }]);
            onAdded();
        }
    }, [user, onAdded]);

    // Lọc danh sách theo keyword
    const filteredUsers = users.filter(
        (u) =>
            u.name.toLowerCase().includes(keyword.toLowerCase()) ||
            u.username.toLowerCase().includes(keyword.toLowerCase())
    );

    // Hàm sửa người dùng (Deep Copy)
    function editUser(user) {
        setEditing({ ...user, address: { ...user.address } });
    }

    // Xử lý thay đổi khi chỉnh sửa
    function handleEditChange(field, value) {
        if (["street", "suite", "city"].includes(field)) {
            setEditing({ ...editing, address: { ...editing.address, [field]: value } });
        } else {
            setEditing({ ...editing, [field]: value });
        }
    }

    // Lưu sau khi chỉnh sửa
    function saveUser() {
        if (editing.name === "" || editing.username === "") {
            alert("Vui lòng nhập Name và Username!");
            return;
        }
        setUsers(prev => prev.map(u => u.id === editing.id ? editing : u));
        setEditing(null);
    }

    // Xóa người dùng
    function removeUser(id) {
        setUsers((prev) => prev.filter((u) => u.id !== id));
    }

    if (loading) {
        return <div className="loading">Đang tải dữ liệu...</div>;
    }

    return (
        <div>
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Username</th>
                        <th>Email</th>
                        <th>City</th>
                        <th>Hành động</th>
                    </tr>
                </thead>
                <tbody>
                    {filteredUsers.length === 0 ? (
                        <tr>
                            <td colSpan="6" className="no-data">
                                Không tìm thấy người dùng nào
                            </td>
                        </tr>
                    ) : (
                        filteredUsers.map((u) => (
                            <tr key={u.id}>
                                <td>{u.id}</td>
                                <td>{u.name}</td>
                                <td>{u.username}</td>
                                <td>{u.email}</td>
                                <td>{u.address.city}</td>
                                <td>
                                    <button className="btn-edit" onClick={() => editUser(u)}>
                                        Sửa
                                    </button>
                                    <button className="btn-delete" onClick={() => removeUser(u.id)}>
                                        Xóa
                                    </button>
                                </td>
                            </tr>
                        ))
                    )}
                </tbody>
            </table>

            {/* Modal chỉnh sửa */}
            {editing && (
                <div className="modal-overlay">
                    <div className="modal-content">
                        <h4>Chỉnh sửa người dùng</h4>
                        <div className="form-group">
                            <label htmlFor="edit-name">Name:</label>
                            <input
                                id="edit-name"
                                type="text"
                                value={editing.name}
                                onChange={(e) => handleEditChange("name", e.target.value)}
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="edit-username">Username:</label>
                            <input
                                id="edit-username"
                                type="text"
                                value={editing.username}
                                onChange={(e) => handleEditChange("username", e.target.value)}
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="edit-email">Email:</label>
                            <input
                                id="edit-email"
                                type="email"
                                value={editing.email}
                                onChange={(e) => handleEditChange("email", e.target.value)}
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="edit-street">Street:</label>
                            <input
                                id="edit-street"
                                type="text"
                                value={editing.address.street}
                                onChange={(e) => handleEditChange("street", e.target.value)}
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="edit-suite">Suite:</label>
                            <input
                                id="edit-suite"
                                type="text"
                                value={editing.address.suite}
                                onChange={(e) => handleEditChange("suite", e.target.value)}
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="edit-city">City:</label>
                            <input
                                id="edit-city"
                                type="text"
                                value={editing.address.city}
                                onChange={(e) => handleEditChange("city", e.target.value)}
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="edit-phone">Phone:</label>
                            <input
                                id="edit-phone"
                                type="text"
                                value={editing.phone}
                                onChange={(e) => handleEditChange("phone", e.target.value)}
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="edit-website">Website:</label>
                            <input
                                id="edit-website"
                                type="text"
                                value={editing.website}
                                onChange={(e) => handleEditChange("website", e.target.value)}
                            />
                        </div>
                        <div className="form-actions">
                            <button className="btn-save" onClick={saveUser}>
                                Lưu
                            </button>
                            <button className="btn-cancel" onClick={() => setEditing(null)}>
                                Hủy
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

export default ResultTable;
